//http://localhost:3000/note/y03q8eftjr6sbfdh43zl7d20
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import env from '../env.json';
import Error from "./Error";


function Note() {
    let { noteURL } = useParams();
    const [noteText, setNoteText] = useState('');
    const [lineClass, setLineClass] = useState('hide');
    const [formClass, setFormClass] = useState('hide'); // скрываем
    const [errorClass, setErrorClass] = useState('hide'); // скрываем


    useEffect(() => {
        if (noteURL !== undefined) {
            fetch(env.urlBackend, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                },
                body: JSON.stringify({ "url": noteURL })
            })
                .then(response => response.json())
                .then(response => {
                    console.log(response);
                    if (response.result) {
                        setNoteText(response.note);
                        setLineClass('');
                        setFormClass('hide');
                        setErrorClass('hide');
                    }
                    else if (!response.result) {
                        setLineClass('hide');
                        setFormClass('hide');
                        setErrorClass('');
                    }
                })
        }
        else {
            setLineClass('hide');
            setFormClass('');
            setErrorClass('hide');
        }
    }, []);

    function getNote(event) {
        event.preventDefault();
        let url = event.target.elements.url.value;
        url = url.trim();
        if (url === '') {
            alert('Заполните поля');
            return false;
        }
        noteURL = url;
        window.location.href = env.url + '/' + url;
    }
    function searchNote() {
        window.location.href = env.url;
    }

    return (
        <>


            <div>
                <div className={lineClass}>
                    {/* <h4>Note:</h4> */}
                    <div>{noteText}</div>
                    <div> <button type="submit" className="btn btn-primary" onClick={searchNote}>Смотреть еще одну заметку(note)</button></div>
                </div>

                <div className={formClass}>
                    <form action="" onSubmit={getNote}>
                        <label htmlFor="url"></label>
                        <input type="text" name="url" id="url" className="form-control" defaultValue="Введите hash заметки" />
                        <button type="submit" className="btn btn-primary">Искать Note</button>
                    </form>
                </div>
            </div>
        </>
    );

}

export default Note;
