        super();
        this.state = {
            s1: props.p1,
            s2: 201
        }
    }
    buttonHendler = () => {
        let val = this.state.s1
        let val2 = this.state.s2
        val = (val + 50)
        val2 = (val2 + 50)
        this.setState({
            s1: val,
            s2: val2
        });
    }
    componentDidMount() {

    }

    render() {
        return (
            <>
                <div>
                    <button onClick={this.buttonHendler}>Push me</button>
                </div>
                <div> {this.state.s1}</div>
                <div>{this.state.s2}</div>




            </>
        )
    }
}


